mapper.update:
update tb_data_info set modify_time=NOW(),

  data_status=#{dataStatus},

plain_text=#{plainText}
where account=#{account}
and data_id = #{dataId}
and data_sub_id = #{dataSubId}

mapper.select:
select * from tb_data_info
where 1=1

  and account = #{param.account}


  and data_id = #{param.dataId}


  and data_sub_id = #{param.dataSubId}

mapper.delete:
delete from tb_data_info
where account = #{param.account}
and data_id = #{param.dataId}
and data_sub_id = #{param.dataSubId}

mapper.select:
select count(1) from tb_data_info
where account = #{param.account}
and data_id = #{param.dataId}
and data_sub_id = #{param.dataSubId}

select count(1) from tb_data_info where 1=1

  and account = #{account}


  and data_id = #{dataId}


  and data_sub_id = #{dataSubId}


  and data_status = #{dataStatus}

select * from tb_data_info
where 1=1

  and account = #{param.account}


  and data_id = #{param.dataId}


  and data_sub_id = #{param.dataSubId}


  and data_status = #{param.dataStatus}


  order by create_time ${param.flagSortedByTime}


  limit #{param.start},#{param.pageSize}

select data_id from tb_data_info where 1=1

  and account = #{account}


  and data_status = #{dataStatus}

group by data_id

select data_id from tb_data_info where 1=1

  and account = #{account}

and plain_text = #{plainText}
group by data_id

select plain_text from tb_data_info
where account = #{account} and data_sub_id="value" and data_id in
(select data_id from tb_data_info
where account = #{account}
and data_sub_id="status"
and plain_text = #{status}
group by data_id);
