mapper.update:
update tb_data_info set modify_time=UTC_TIMESTAMP(),

  data_status=#{dataStatus},

data_field_value = #{dataFieldValue}
where account = #{account}
and data_entity_id = #{dataEntityId}
and data_field_id = #{dataFieldId}

mapper.select:
select * from tb_data_info
where 1=1

  and account = #{param.account}


  and data_entity_id = #{param.dataEntityId}


  and data_field_id = #{param.dataFieldId}

mapper.delete:
delete from tb_data_info
where account = #{param.account}
and data_entity_id = #{param.dataEntityId}
and data_sub_id = #{param.dataFieldId}

mapper.select:
select count(1) from tb_data_info
where account = #{param.account}
and data_entity_id = #{param.dataEntityId}
and data_field_id = #{param.dataFieldId}

select count(1) from tb_data_info where 1=1

  and account = #{account}


  and data_entity_id = #{dataEntityId}


  and data_field_id = #{dataFieldId}


  and data_status = #{dataStatus}

select * from tb_data_info
where 1=1

  and account = #{param.account}


  and data_entity_id = #{param.dataEntityId}


  and data_field_id = #{param.dataFieldId}


  and data_status = #{param.dataStatus}


  order by create_time ${param.flagSortedByTime}


  limit #{param.start},#{param.pageSize}

select data_entity_id from tb_data_info where 1=1

  and account = #{account}


  and data_status = #{dataStatus}

group by data_entity_id

select data_entity_id from tb_data_info where 1=1

  and account = #{account}

and data_field_id = "status"
and data_field_value = #{dataFieldValue}
group by data_entity_id

select data_entity_id,data_field_value from tb_data_info
where account = #{account} and data_field_id = "value" and data_entity_id in
(select data_entity_id from tb_data_info
where account = #{account}
and data_field_id = "status"
and data_field_value = #{status});

select data_field_value from tb_data_info
where account = #{account} and data_field_id = "value" and data_entity_id in
(select data_entity_id from tb_data_info
where account = #{account}
and data_field_id = "status"
and data_field_value = #{status}
group by data_entity_id);

mapper.update:
update tb_data_info set data_field_value = #{desCreditStatus}, modify_time = UTC_TIMESTAMP()
where account = #{account}
and data_entity_id = #{dataEntityId}
and data_field_id = #{dataFieldId}
and data_field_value = #{srcCreditStatus}
